import { Card } from "src/app/models/Card.model";
import { environment as env } from "src/environments/environment";
import * as i0 from "@angular/core";
import * as i1 from "src/app/services/load-pics.service";
import * as i2 from "./deck/deck.component";
import * as i3 from "./main/main.component";
export class GameBoardComponent {
    constructor(picsLoader) {
        this.picsLoader = picsLoader;
        this.deck1 = [];
        this.deck2 = [];
        this.types = ["dog", "cat", "duck"];
        this.adjectives = [
            "powerfull",
            "scary",
            "killer",
            "coward",
            "ferocious",
            "wikling",
            "magestic",
        ];
        this.generateCards(1);
        this.generateCards(2);
    }
    ngOnInit() { }
    async generateCards(deckNum) {
        for (let i = 0; i < env.numOfCardsInDeck; i++) {
            const type = this.types[Math.floor(Math.random() * this.types.length)];
            let url;
            switch (type) {
                case "dog":
                    url = await this.picsLoader.loadDogImg();
                    break;
                case "cat":
                    url = await this.picsLoader.loadCatImg();
                    break;
                case "duck":
                    url = await this.picsLoader.loadDuckImg();
                    break;
            }
            const name = this.adjectives[Math.floor(Math.random() * this.adjectives.length)];
            const power = Math.floor(Math.random() * 5);
            const newCard = new Card(name, type, url, power);
        }
    }
}
